INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Overordnede krav til arkivstrukturen','Type','Merknad','Forklaring','Konsekvens','Konf.nivå','Ref.krav','Status','Ansv.');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.1.1','For at et system skal kunne godkjennes etter Noark5-standarden, må den konseptuelle modellen av arkivstrukturen og de funksjonelle muligheter den gir, kunne implementeres i det aktuelle systemets (fysiske) datastrukturer.','O','','','','','',' ','TEJ');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.1.2','Arkivdokumenter som tilhører et sakarkiv skal inngå i en arkivstruktur som skal inneholde følgende arkivenheter: Arkiv, Arkivdel, Klassifikasjonssystem, Klasse, Mappe 1), Registrering2), Dokumentbeskrivelse og Dokumentobjekt.
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.1.3','Arkivdokumenter som ikke tilhører et sakarkiv (eks. fagsystem) kan inngå i en ”forenklet” arkivstruktur som minimum skal kunne inneholde følgende arkivenheter: Arkiv, Arkivdel, Registrering1) og Dokumentobjekt. 1) Registrering er en fellesbenevning for arkivenhetene Registrering inkl. spesialiseringene Basisregistrering, Journalpost og Møteregistrering.','O','Erstattes av krav 5.1.2 for sakarkiver. Dette er en minimumsløsning. Fagsystemer kan bestå av de samme arkiv­enheter som sakarkiv.','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.1.4','Noark5 skal ha tjenester/funksjoner for å lagre, gjenfinne, endre og slette data og utvalg1) av data i henhold til metadatabeskrivelsene i alle arkivenheter (se krav 5.1.2) og tilhørende klasser som er dokumentert i de konseptuelle modellene og metadatatabellene i Noark5. Merk: Funksjonelle enkeltkrav i de forskjellige kapitlene kan overstyre dette kravet. 1)Med utvalg av data menes utvalg innenfor en fra/til angivelse av obligatoriske metadata for aktuell arkivenhet.','O','Dette er et overordnet krav som omfatter mye funksjonalitet i Noark5 kjerne.','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.1.5','En arkivenhet (se krav 5.1.2 og 5.1.3) skal kunne identifiseres entydig innenfor det arkivskapende organet. I et arkivuttrekk skal denne identifikasjonen hete systemID, og være entydig på tvers av alle uttrekk som organet produserer, dermed også på tvers av alle systemer organet benytter.  Også arkivenheter som dupliseres i et arkivuttrekk, skal identifiseres entydig, slik at identiske arkivenheter har ulik systemID.','O','','','','','','','');
