INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.2.1','En Noark5-løsning skal kunne bestå av ett eller flere selvstendige Arkiv.','O','','','','','','','');
