INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.2.10','Et Arkiv bør kunne inndeles i et hierarki (skissert i modellen ved bruk av egenrelasjon) av Underarkiver. Merknad: Det bør være mulig med ett eller flere nivåer under Arkiv, f.eks. for å representere fysiske delarkiver. Dette kan være aktuelt for virksomheter som har arkiver fysisk plassert på flere forskjellige steder.','V','','Dette kravet bør stilles dersom kommunen fører eller kan komme til å føre arkiv for interkommunale selskaper, kommunale foretak, kommunale aksjeselskap eller lignende.Tillater at man oppretter underarkiver, i mindre grad nødvendig ved helelektronisk arkiv','Utelates kravet vil det ikke kunne opprettes underarkiv','','5.2.12','','');
