INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.2.11','Systemet bør ha en tjeneste/funksjon for å angi et Arkiv som Underarkiv til et Arkiv.','V','','Dette kravet bør velges dersom 5.2.10 velges','','','5.2.12','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.2.12','Et Underarkiv skal kun opprettes og endres gjennom Administrasjonssystemet for Noark5.','B','Obligatorisk dersom underarkiv brukes.','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Strukturelle krav til Arkivdel','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.2.13','En Arkivdel kan ha registrert ingen eller ett preferert Klassifikasjonssystem og et Klassifikasjonssystem kan inngå i ingen, en eller flere Arkivdel(er).','B','Obligatorisk for sakarkiv.','','','','systemtype','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.2.14','En Arkivdel kan ha registrert ingen eller en Skjerming og en Skjerming kan inngå i ingen, en eller flere Arkivdeler.','O','Se merknad nedenfor.','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.2.15','En Arkivdel kan ha registrert ingen eller en Bevaring og kassasjon og en Bevaring og kassasjon kan inngå i ingen, en eller flere Arkivdeler.','O','Se merknad nedenfor.','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.2.16','En Arkivdel kan ha tilknyttet (inneholde) ingen, en eller flere Mapper.','O','','','','','','','');
