INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.2.19','Dersom Arkivdel er registrert som avsluttet (avsluttetDato er satt) skal det ikke være mulig å legge til flere tilhørende Mapper eller Registreringer','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.2.20','En arkivdel skal inneholde informasjon om hvilken status arkivperioden har. Autoriserte brukere skal kunne endre statusverdier. Obligatoriske verdier er: 1. Aktiv periode 2. Overlappingsperiode 3. Avsluttet periode Andre verdier kan brukes ved behov.','B','Obligatorisk for sakarkiver, og for alle løsninger hvor det foretas periodisering.','','','','systemtype','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.2.21','En arkivdel skal inneholde dato for når arkivperioden starter.','B','Obligatorisk for sakarkiver, og for alle løsninger hvor det foretas periodisering.','','','','systemtype','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.2.22','En avsluttet arkivdel skal inneholde dato for når perioden ble avsluttet.','B','Obligatorisk for sakarkiver, og for alle løsninger hvor det foretas periodisering. Må tildeles en verdi når perioden avsluttes.','','','','systemtype','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.2.23','En arkivdel skal inneholde informasjon om de tilhørende dokumentene er fysiske eller elektroniske.','B','Obligatorisk for blandet fysisk og elektronisk arkiv.','','','','Systemtype','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Strukturelle krav til Klassifikasjonssystem og Klasse','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.3.1','Et Klassifikasjonssystem skal kunne inneholde ingen, en eller flere Klasser og en Klasse kan tilhøre kun ett Klassifikasjonssystem.','B','Obligatorisk for sakarkiv','','','','systemtype','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.3.2','Et Klassifikasjonssystem kan inngå som primært system i ingen, en eller flere Arkivdeler','B','Obligatorisk for sakarkiv','','','','systemtype','','');
