INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.3.5','En Klasse kan ha registrert ingen eller en Bevaring og kassasjon og en Bevaring og kassasjon kan inngå i ingen, en eller flere Klasser','B','Obligatorisk for sakarkiv. Se merknad 1 nedenfor.','','','','systemtype','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.3.6','En Klasse kan ha registrert ingen, ett eller flere Nøkkelord og et Nøkkelord bør kunne inngå i ingen, ett eller flere Klasser','V','Dette valgfrie kravet kan erstattes med følgende egendefinerte krav: Det skal være mulig å registrere nøkkelord på klasse, mappe og basisregistrering. Gjelder også kravene 5.7.1 til 5.7.3.','Nøkkelord er et foklarende ord til klassen.','Gjør det lettere å søke etter rett klasse ved at en kan søke på nøkkleord og ikke bare klassenavn','','','','');
