INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.3.8','Et klassifikasjonssystem bør kunne beskrives med forskjellige klassifikasjonstyper. Eksempel på verdier: "Funksjonsbasert, "Emnebasert, hierarkisk arkivnøkkel", "Emnebasert, ett nivå", "K-koder", "Mangefasettert, ikke hierarki",hierarkisk" "Objektbasert", "Fødselsnummer", "Gårds- og bruksnummer"','V','Aktuelt der det finnes mer enn ett klassifikasjons­system.','','','','','','');
