INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.3.12','Det skal finnes en tjeneste/funksjon for å ajourholde et hierarki av Klasser.','B','Obligatorisk for sakarkiv','','','','systemtype','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.3.13','Det skal finnes en tjeneste/funksjon for å angi om en verdi av Klasse skal/skal ikke benyttes ved klassifikasjon av saker.','B',' For sakarkiver skal det være mulig å stenge (avslutte) klasser slik at de ikke lenger kan benyttes.','','','','systemtype','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.3.14','For at en Klasse skal kunne tilordnes en Mappe, må den ligge på nederste nivå i klassehierarkiet.','B','Obligatorisk for sakarkiv. En klasse kan altså ikke inneholde både andre klasser og mapper.','','','','systemtype','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.3.15','Dersom verdien i Klasse er registrert som avsluttet (avsluttetDato), skal det ikke være mulig å tilordne nye Mapper til Klassen.','B','Obligatorisk dersom det er mulig å avslutte klasser.','','','','systemtype','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.3.16','Det skal logges når en klasse ble opprettet, og hvem som opprettet den. Bare autorisert personale kan opprette klasser. Andre brukere kan gis tillatelse til å opprette klasser.','B','Obligatorisk for sakarkiv. Ofte vil alle klasser legges inn før systemet tas i bruk. Men det kan gis tillatelse til at klasser opprettes fortløpende, noe som særlig er aktuelt ved objektbasert klassifikasjon.','','','','systemtype','','');
