INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.4.5','En Mappe bør kunne inngå i andre Mapper i et hierarki. Disse er kalt undermapper, og er i modellen skissert med en egenrelasjon.','V','','Dokumenter i en sak kan lagres i mapper inne i saksmappa. Det kan være flere nivåer på dette med mapper i mapper.','Lar deg gruppere dokumenter i en sak etter innhold. Velges dette bort vil alle dokumenter i en sak lagres kronologisk i saksmappa.','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.4.6','En Mappe skal kunne bestå av ingen, en eller flere Registreringer og en Registrering kan inngå i (kun) en Mappe. ','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.4.7','Dersom en Mappe er registrert som avsluttet (avsluttetDato) skal det ikke være mulig å legge flere Registreringer til Mappen.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.4.8','En Mappe skal kunne utvides til en Saksmappe.','B','Obligatorisk for sakarkiv.','','','','systemtype','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','For et sakarkiv skal det kunne benyttes en spesialisert mappetype: Saksmappe. En spesialisert klasse kan betegnes som en utvidelse av hovedklassen.','','','','','','systemtype','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.4.9','En Saksmappe skal kunne identifiseres entydig innenfor arkivet. Det anbefales at denne identifikasjonen er en kombinasjon av saksår og et forløpende sekvensnummer for saksmappene innenfor året.','B','Andre måter å identifisere saksmapper kan godtas.','Kravet er obligatorisk for sakarkiv, og det bør kreves at den anbefalte identifikasjonen følges for sak-/arkivsystem.','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.4.10','En Saksmappe skal kunne ha registrert ingen, en eller flere Sekundærklassering og en Sekundærklassering tilhører kun en Saksmappe og kun en Klasse.','B','Obligatorisk for saksmapper.','','','','systemtype','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.4.11','En Saksmappe bør kunne ha registrert ingen eller en Journalenhet og en Journalenhet kan inngå i ingen, en eller flere Saksmapper.','V','Journalenhet er ikke lenger obligatorisk for sakarkiver.','Journalenhet er de som journalfører saker. Har du flere enheter som journalfører selv velger du dette kravet.','Lar deg spesifisere hvilken enhet som journalfører. Velges kravet bort vil du ikke kunne velge journalførende enhet.','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.4.12','En Saksmappe skal kunne ha registrert ingen eller en Administrativ enhet og en Administrativ enhet kan inngå i ingen, en eller flere Saksmapper.','B','Obligatorisk for sakarkiv.','','','','systemtype','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.4.13','En Saksmappe skal kunne inneha ingen, en eller flere Saksparter og en Sakspart skal alltid tilhøre en Saksmappe.','B','Obligatorisk for løsninger hvor det inngår saksparter Se også merknad nedenfor.','','','','','','');
