INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.4.14','Dersom det er angitt et primært klassifikasjonssystem for Arkivdel, skal alle Mapper i arkivdelen ha verdier fra dette klassifikasjonssystemet som primær klasse.','B','Obligatorisk dersom primært klassifika­sjonssystem er angitt for arkivedel.','Kravet bør settes obligatorisk for sak-/arkivsystem','','','systemtype','','');
