INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.4.16','Det bør finnes en tjeneste/funksjon for å ajourholde Journalenhet på en Saksmappe.','V','','Kravet bør stilles som obligatorisk, dersom kommunen opererer med flere ulike journalenheter','Gjør det mulig å endre journalførende enhet på enkelte saksmapper','','','','');
