INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.4.19','Det bør finnes en tjeneste/funksjon for å legge opp og ajourholde undermapper for en Mappe (mappehierarki).','V','','Kravet bør stilles som obligatorisk dersom man har valgt krav nr. 5.4.5 (mappe i mappe)','','','','','');
