INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.10.24','Oversikten skal inneholde de viktigste metadata for dokumentene, inkludert metadata for bevaring og kassasjon.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.10.25','Det bør være mulig å åpne et dokument for presentasjon av innhold direkte fra denne oversikten.','V','','','Anbefales fordi dette vil være tidsbesparende','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.10.26','Autoriserte brukere bør kunne endre metadata for bevaring og kassasjon for de enkelte dokumenter direkte fra oversikten.','V','','','Anbefales fordi dette vil være tidsbesparende','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.10.27','Det skal finnes en funksjon for å kassere alle dokumenter som har verdien "Kasseres" som kassasjonsvedtak, og hvor bevaringsdatoen er eldre enn dagens dato. En slik funksjon skal kunne begrenses til et mindre utvalg dokumenter.','B','Obligatorisk i løsninger hvor kassasjon skal skje og ved behov for skille mellom kassable og ikke kassable dokumenter.','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.10.28','Det skal ikke være mulig å sette kassasjonsvedtak "Kasseres" på en mappe som er registrert som presedenssak.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.10.29','Kassasjonen skal kunne utføres automatisk for hele utvalget dokumenter, men det skal også være mulig å be om spørsmål om kassasjon skal utføres for hvert enkelt dokument.','B','Obligatorisk når 5.10.27 oppfylles','','','','5.10.27','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.10.30','Bare autoriserte brukere kan starte en funksjon for kassasjon av dokumenter.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.10.31','Alle versjoner, varianter og formater av dokumentet skal omfattes av kassasjonen.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.10.32','Kassasjon skal innebære at all metadata om dokumentobjektet slettes. Selve dokumentet skal slettes fra filsystemet dersom dokumentet (dokumentbeskrivelsen) ikke er knyttet til andre registreringer.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.10.33','Funksjonen for kassasjon bør være i to trinn, slik at det i første omgang er mulig å gjenopprette de kasserte dokumentene. Endelig sletting av dokumentobjekt og dokument skal kunne skje på et senere tidspunkt.','V','','','Anbefales fordi kassasjon er uopprettelig','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.10.34','Metadata om dokumentet ned til dokumentbeskrivelse, skal i utgangspunktet ikke slettes selv om dokumentet kasseres.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.10.35','For hvert dokument som blir kassert, skal det på dokumentbeskrivelsesnivå logges dato for kassasjon og hvem som utførte kassasjonen.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.10.36','','','Utgår','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Strukturelle krav til periodisering','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.11.1','En arkivdel skal kunne inneholde en tekstlig beskrivelse av hvilke prinsipper den skal periodiseres etter.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.11.2','En arkivdel skal inneholde referanser til eventuelle forløpere og arvtakere.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Funksjonelle krav til periodisering','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.11.3','Det skal være mulig å knytte nyopprettede mapper til en arkivdel som inneholder en aktiv arkivperiode.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.11.4','En arkivdel som inneholder en overlappingsperiode, skal være sperret for tilføyelse av nyopprettede mapper. Men eksisterende mapper i en overlappingsperiode skal være åpne for nye registreringer.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.11.5','Dersom en ny registrering føyes til en mappe som tilhører en arkivdel i overlappingsperiode, skal mappen automatisk overføres til arkivdelens arvtaker.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.11.6','En arkivdel som inneholder en avsluttet arkivperiode, skal være sperret for tilføyelse av nye mapper. Alle mapper skal være lukket, slik at heller ingen registreringer og dokumenter kan føyes til.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.11.7','Det skal være umulig å avslutte en arkivdel i overlappingsperiode dersom den fremdeles inneholder åpne mapper.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.11.8','Det skal være mulig å få en oversikt over mapper som fremdeles er åpne i en overlappingsperiode.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.11.9','Det skal være mulig å overføre åpne mapper fra en arkivdel i en overlappingsperiode til arkivdelens arvtaker.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.11.10','Det bør være mulig å overføre i en samlet, automatisert prosess.','V','','hvorfor er dette en V-krav?','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.11.11','Det skal være mulig å flytte avsluttede mapper til en arkivdel som inneholder en avsluttet periode.','B','Obligatorisk for funksjon for periodisering','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.11.12','Det skal være mulig å flytte en gruppe av avsluttede mapper til en arkivdel som inneholder en avsluttet periode i en automatisert prosess.','B','Obligatorisk for generelle sakarkiver','','','','sakarkiv','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.11.13','Dersom dokumentene i en arkivdel er ikke-elektroniske (fysiske), skal det også være mulig å registrere oppbevaringssted.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Overordnede krav til arkivuttrekk','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.1 ','Det skal være mulig å produsere arkivuttrekk bestående av arkivdokumenter, journalrapporter, metadata, endringslogg og XML-skjemaer. ','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','Dette gjelder jo all sak/arkiv system. Kanskje noen vil bruke noark for data som ikke er bevaringsverdi!!!','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.2 ','Arkivuttrekket skal utgjøre en avleveringspakke (Submission Information Packages), slik dette er definert i ISO 14571 OAIS.','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','Dette er en tullete krav som gir ingen mening! RA tvinger folk I ISO 14571, men de selv forklarer hvordan uttrekket ser ut. Et AIP o IAIS modellen er et teoretisk ting, et n5 uttrekk er en implementasjon av en SIP','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.3 ','Formatet på metadata, endringslogg og journalrapporter i arkivuttrekket skal være XML (XML 1.0).','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.4 ','Tegnsettet til alle XML-filer skal være UTF-8.','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','Problematisk hvis database inneholder ISO-8859-1/ ISO-8859-4 (samiske tegn). Nåværedede tjenestegrensesnitt inneholder muligheten for UTF-16/32. Noen DBMS har egne tegnsett tror jeg!','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.5 ','Metadataelementer som ikke har verdi, skal utelates fra arkivuttrekket. I uttrekket skal det med andre ord ikke forekomme tomme elementer med kun start- og slutt-tagg.','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.6 ','Alfanumeriske verdier i arkivuttrekket skal representeres vha. XML Schema 1.0 -datatypen string.','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.7 ','Datoer uten klokkeslett i arkivuttrekket skal representeres vha. XML Schema 1.0 -datatypen date.','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.8 ','Datoer med klokkeslett i arkivuttrekket skal representeres vha. XML Schema 1.0 -datatypen dateTime.','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.9 ','Heltall i arkivuttrekket skal representeres vha. XML Schema 1.0-datatypen integer. ','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.10 ','Format på arkivdokumenter i arkivuttrekket skal være et av arkivformatene definert i § 8-17 i Forskrift til arkivloven av 1. desember 1999 nr. 1566 om utfyllende tekniske og arkivfaglige bestemmelser om behandling av offentlige arkiver, kapittel VIII.','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','Et veldig vanskelig punkt. Skal systemet ikke tillate filformater det ikke finnes godkjente rkivformat på','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.11 ','Organiseringen av filene i arkivuttrekket skal følge Forskrift til arkivloven av 1. desember 1999 nr. 1566 om utfyllende tekniske og arkivfaglige bestemmelser om behandling av offentlige arkiver, kapittel VIII, så langt disse er relevante.','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','må lese denne','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til innholdet i en avleveringspakke','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.12 ','Et arkivuttrekk skal omfatte en avsluttet arkivperiode, og bestå av innholdet i en eller flere avsluttede arkivdeler.','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','Alle mapper må være avskrevet og innkommende brev må avskrevet','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.13 ','Hele klassifikasjonsstrukturen, dvs. alle klasser i et klassifikasjonssystem, skal inngå i hver enkelt avleveringspakke. Sekundære klassifikasjonssystemer kan også være med, men klassene her skal ikke inneholde mapper.','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','bøt være uproblematisk, men man må være obs på emnebaser klassifikasjon der feks mennesker er identifisert. Jeg tenker ved oppgradering eller import av eldre base om det kan finnes klasser av person identifikator der det ikke er identifsert at informasjon skal være skjermet','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.14 ','Det bør være mulig å produsere et arkivuttrekk på grunnlag av en startdato og en sluttdato, uavhengig av tilhørighet til arkivdel og om mappene er avsluttet eller ikke.','V','Kravet gjelder særlig ved migrering.','kan du få referanse problemer dersom en mappe er koblet til en arkivdel med referanseArkivdel som ikke er med I uttrekket??','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.15 ','Filene i en avleveringspakke skal ligge under en felles overordnet filkatalog kalt avleveringspakke.
