INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.16 ','Alle XML-filer som inngår i en avleveringspakke, skal være definert vha. medfølgende XML-skjema.  ','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','','','','arkivstruktur','','');
