INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.18 ','For arkivuttrekk.xml, arkivstruktur.xml, endringslogg.xml, loependeJournal.xml og offentligJournal.xml skal kun de tilhørende skjemaene som er tilgjengelige fra Arkivverket, benyttes i avleveringspakken. Varianter av skjemaene skal ikke benyttes.','O','','','','','arkivstruktur','','');
