INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.24 ','En avleveringspakke skal inneholde en fil med metadata for arkivdokumentene som inngår i pakken.  Alle metadataelementene skal være nøstet inn i en sammenhengende, hierarkisk struktur.','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','forstår ikke kravet. En fil? Er ikke dette bare arkivstruktur.xml??','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.25 ','Alle metadataelementer som er merket med "A" i kolonnen "Avl." i vedlegget "Metadata gruppert på objekter" skal være med i arkivuttrekket, såfremt de er tilordnet verdier.','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','burde være uproblematisk, men hva med elementer som er oblig som ikke har en verdi?','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.26 ','Alle forekomster av arkivenheter i arkivstrukturen, unntatt dokumentobjekt, skal være identifisert med en entydig identifikasjon. Denne identifikasjonen skal være entydig for alle arkivuttrekk som produseres av en arkivskaper, jf. krav 5.1.5.','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.27 ','Metadata for arkivdokumenter som er kassert før arkivuttrekket produseres, skal være med i uttrekket. Disse metadataene skal omfatte alle arkivenheter ned til dokumentbeskrivelse, og her skal det også ligge logginformasjon om kassasjonen.','B','Obligatorisk for sakarkiver.','','','','saksarkiv','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til Endringslogg','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.28 ','En avleveringspakke skal inneholde en endringslogg for metadata som har fått en ny verdi.  Hvilke metadata dette gjelder, og når logging av disse endringene skal utføres, går fram av vedlegg 3 "Oversikt over metadata hvor det skal logges at det gjøres endringer i innholdet.','B','Obligatorisk der det kan være aktuelt med avlevering til arkivdepot','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til journalrapportene','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.29 ','En avleveringspakke skal inneholde både en løpende journal og en offentlig journal. Journalene skal omfatte samme tidsrom som arkivperioden. ','B','Obligatorisk for arkiver med korrespondanse-dokumenter som det kan være aktuelt å avlevere til arkivdepot','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.30 ','Journalrapportene skal inneholde alle registreringer av typen journalpost som er journalført i løpet av arkivperioden. Journalpostene skal være sortert kronologisk etter løpenummer (journalår og sekvensnummer). ','B','Obligatorisk for arkiver med korrespondanse­dokumenter som det kan være aktuelt å avlevere til arkivdepot','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til virksomhetsspesifikke metadata','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.31 ','Hvis virksomhetsspesifikke metadata skal inngå som en del av arkivuttrekket, skal de knyttes til mappe, basisregistrering eller sakspart i arkivstruktur.xml gjennom elementet virksomhetsspesifikkeMetadata.','B','Obligatorisk ved bruk av virksomhetsspesifikke metadata.','litt usikker om vi bør diskutere dette en gang til','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.32 ','Alle virksomhetsspesifikke metadataelementer skal være definert i ett eller flere medfølgende XML-skjemaer.','B','Obligatorisk ved bruk av virksomhetsspesifikke metadata','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.33 ','Når virksomhetsspesifikke metadata inngår som en del av arkivuttrekket, skal det finnes referanse til aktuelle skjemaer i arkivstruktur.xml.','B','Obligatorisk ved bruk av virksomhetsspesifikke metadata','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.34 ','Virksomhetsspesifikke metadataelementer skal være tilordnet et namespace gjennom tilhørende XML-skjema.','B','Obligatorisk ved bruk av virksomhetsspesifikke metadata','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.35 ','Innholdet og betydningen av hvert virksomhetsspesifikt metadataelement skal dokumenteres mer inngående i aktuelt XML skjema dersom det ikke er innlysende hva elementet inneholder. Denne dokumentasjonen skal være i form av XML Schema elementene annotation og documentation knyttet til definisjonen av det enkelte metadataelementet i aktuelt skjema.','B','Obligatorisk ved bruk av virksomhetsspesifikke metadata','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til arkivdokumentene','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.36 ','En avleveringspakke skal inneholde arkivdokumenter i arkivformat. Hvert dokument skal eksporteres som én dokumentfil','B','Obligatorisk der det kan være aktuelt med avlevering av elektroniske arkivdokumenter til arkivdepot','betyr at dokumenter som må kompileres til en enhet må kunne kompileres. Feks tenk en epost som er html med tekst og bilder. Alt skal konverteres til feks pdf','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.37 ','Hver arkivert versjon av et dokument skal eksporteres som en egen dokumentfil.','B','Obligatorisk der det kan være aktuelt med avlevering av elektroniske arkivdokumenter til arkivdepot','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.38 ','Hver arkivert variant av et dokument skal eksporteres som en egen dokumentfil.','B','Obligatorisk der det kan være aktuelt med avlevering av elektroniske arkivdokumenter til arkivdepot','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.39 ','I et sakarkiv skal også dokumenter som er knyttet til registreringer av typen Registrering (dvs. dokumenter som er arkivert uten journalføring) inngå i arkivuttrekket.','B','Obligatorisk for sakarkiver hvor dokumenter er arkivert uten journalføring.','','','','saksarkiv','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.40 ','Hvert dokumentobjekt i arkivstruktur.xml skal ha en referanse til en dokumentfil i avleveringspakken. Det skal ikke forekomme referanser til dokumenter som ikke finnes i pakken, og det må ikke være dokumenter i pakken som det ikke blir referert til. Referansen fra arkivstrukturen skal være relativ til dokumentfilene, dvs. inneholde hele "stien" til dokumentet','B','Obligatorisk der det kan være aktuelt med avlevering av elektroniske arkivdokumenter til arkivdepot','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.41 ','Hvert dokumentobjekt i arkivstruktur.xml skal inneholde informasjon om dokumentets format og størrelse. Det skal også inneholde sjekksummen for hvert enkelt dokument, samt algoritmen som ble brukt for å generere sjekksummen. ','B','Obligatorisk der det kan være aktuelt med avlevering av elektroniske arkivdokumenter til arkivdepot','mangelmelding at feltet er definert som streng ikke tall og hva det innebærer','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.12.42 ','Dersom dokumentet er blitt konvertert fra et format til et annet (f.eks. fra produksjonsformat til arkivformat) skal det tilhørende dokumentobjektet i arkivstruktur.xml inneholde informasjon om konverteringen. Er dokumentet blitt konvertert flere ganger, skal alle konverteringene dokumenteres.','O','','','','','arkivstruktur','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til administrasjon av kjernen','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.13.1','Det skal finnes en tjeneste/funksjon for å administrere kjernen','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.13.2','Det må kunne defineres minimum én bruker som er arkivadministrator, som kan logge seg eksplisitt på Noark5 kjernen for å endre konfigurasjon og globale parametere','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.13.3','Det skal finnes en tjeneste/funksjon for administrator for å opprette, redigere og slette arkivenheter (arkiv, arkivdel, klassifikasjonssystem, klasse, mappe, registrering, dokumentbeskrivelse og dokumentobjekt) og tilknyttede metadata som går utover de generelle begrensningene i kapittel 6.1. ','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','Slike registreringer skal logges.','','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.13.4','Et Arkiv og arkivets metadata skal kun opprettes gjennom Administratorfunksjonen for Noark5 kjerne.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.13.5','Et Underarkiv skal kun defineres og endres gjennom Administratorfunksjonen for Noark5 kjerne.','B','Obligatorisk dersom underarkiv brukes','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.13.6','En Arkivdel og arkivdelens metadata skal kun opprettes og endres gjennom Administratorfunksjonen for Noark5 kjerne.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.13.7','Et Klassifikasjonssystem og klassifikasjonssystemets metadata skal kun opprettes og endres gjennom Administratorfunksjonen for Noark5 kjerne.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.13.8','Det skal finnes en tjeneste/funksjon som gjør det mulig for arkivadministrator å gå utover de rollebaserte tilgangsbegrensninger som er definert i løsningen. ','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.13.9','Det bør være mulig å parameterstyre at status ”Dokumentet er ferdigstilt” skal settes automatisk på Dokumentbeskrivelse ved andre statuser på Mappe eller Registrering','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.13.10','Kun autoriserte enheter, roller eller personer skal ha rett til å arkivere en ny versjon av et dokument på en Registrering med status ekspedert, journalført eller avsluttet.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.13.11','Kun autoriserte roller, enheter og personer skal kunne slette inaktive versjoner, varianter og formater av et dokument','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til konvertering til arkivformat','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.13.12','Det skal finnes en tjeneste/funksjon som gjør det mulig for arkivadministrator å angi hvilke dokumentformater som er definert som arkivformater.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.13.13','Det skal finnes en tjeneste/funksjon som gjør at arkivadministrator kan sette opp regler for når (hvilke statuser) arkivdokumenter skal konverteres til arkivformat. ','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.13.14','Det skal være konfigurerbart om dokumenter skal konverteres til arkivformat når status på dokumentbeskrivelse settes til ”Dokumentet er ferdigstilt”.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.13.15','Det skal være konfigurerbart om alle eller spesielt merkede versjoner skal konverteres til arkivformat.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('5.13.16','Det skal finnes en tjeneste/funksjon og rapportering for filformattesting av dokumentene som er lagret i kjernen. Rapporten skal gi oversikt over hvilke mapper, registreringer og/eller dokumentbeskrivelser som ikke inneholder dokumenter lagret i godkjent arkivformat.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til sletting av dokumenter','Type','Merknad','','','','','','');
