INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.6','Hvis registreringsID på Basisregistrering i et sakarkiv benytter det anbefalte formatet åå/nnnnnn-nnnn (dvs. kombinasjonen av saksnummer (mappeID) og dokumentnummer i saken), bør registreringsID endres automatisk. Registreringen bør automatisk tildeles første ledige dokumentnummer i Mappen den flyttes til. ','V','','Dette er en funksjon som bør være automatisert, og bør være et naturlig valg i kommunens kravspesifikasjon','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.7','Registreringer som ikke flyttes i Mappe det flyttes Registreringer fra, bør ikke få endret registreringsID. ','V','','Dette bør være et obligatorisk valg ','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.8','Det bør være mulig å flytte flere Registreringer som er tilknyttet samme Mappe i en samlet operasjon. ','V','','Dette valget kan være arbeidsbesparende. Prioritet må vurderes av kommunen','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.9','Det skal ikke være mulig å flytte en Registrering hvis denne avskriver eller avskrives av andre Registreringer som ikke flyttes. Hvis dette forsøkes skal brukeren få melding om hvilke koblinger som sperrer mot flytting','B','Obligatorisk for sakarkiv','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.10','Flytting av arkivert Registrering skal kun utføres av rollen Administrator.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.11','Det bør være mulig å parameterstyre at andre, spesielt autoriserte brukere også skal kunne ha tillatelse til å utføre flytting av Registreringer.','V','','Kravet anbefales ettersom dette kan lette arkivtjenestens arbeidsbyrde, og føre til at saksbehandler raskere kan jobbe videre','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.12','Det bør være mulig å parameterstyre at alle brukere kan flytte Registreringer de selv er saksbehandler for, hvis status er ”midlertidig registrert” eller ”registrert av saksbehandler”.','V','','kommentar som over','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.13','Ved flytting og renummerering skal bruker få påminnelser om å endre nødvendige referanser på fysiske dokumenter i arkivet','B','Obligatorisk for fysiske arkiv ','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til Avskrivning','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.14','Det skal finnes funksjoner for å få informasjon om restanser.','B','Obligatorisk for sakarkiv','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.15','Det skal finnes en tjeneste/funksjon for å avskrive en Registrering (Journalpost).','B','Obligatorisk for sakarkiv','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.16','Det skal være mulig å avskrive en inngående journalpost med èn eller flere utgående journalposter','B','Obligatorisk for sakarkiv','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.17','Det skal være mulig å la en utgående journalpost avskrive flere inngående journalposter','B','Obligatorisk for sakarkiv','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.18','Når statusen til en mappe settes til avsluttet, skal alle uavskrevne Journalposter av typen ”inngående dokument” eller ”organinternt dokument for oppfølging” som er knyttet til mappen, avskrives med sak avsluttet','B','Obligatorisk for sakarkiv','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.19','Det skal finnes funksjonalitet for at avskriving av organinterne dokument som skal følges opp, skal kunne utføres for hver enkelt mottaker for seg. Dette innebærer at et mottatt, organinternt dokument kan være avskrevet for noen mottakere, men ikke for andre.','B','Obligatorisk for sakarkiv','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.20','Det skal være referanse mellom et innkommet dokument som avskrives av et utgående dokument.','B','Obligatorisk for sakarkiv','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.21','Det skal være referanse mellom et notat som avskrives av et annet notat.','B','Obligatorisk for sakarkiv','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.22','Avskrivning bør  ikke registreres på kopimottakere.','V','','Dette kravet bør stilles.','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til Sakspart','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.23','En Saksmappe skal kunne inneholde et fritt antall Sakspart','B','Obligatorisk for løsninger hvor det inngår parter','6.2.23 - 6.2.25 legges inn i en liste over B-krav som skal oppfylles','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.24','Det skal finnes en tjeneste/funksjon for å ajourholde Sakspart for en Saksmappe','B','Obligatorisk for løsninger hvor det inngår parter','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.25','Sakspart skal kunne skjermes helt eller delvis','B','Obligatorisk for løsninger hvor det inngår parter','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til Presedens','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.26','Det bør være mulig å opprette en presedens knyttet til en sak eller en journalpost','V','','Kravet bør stilles dersom kommunen benytter presedensregistreringer','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.27','Det bør være mulig å opprette et register over hvilke verdier man skal kunne velge presedensHjemmel fra.','V','','Kravet bør stilles dersom kommunen benytter presedensregistreringer','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.28','Det skal være mulig å registrere tidligere presedenser, dvs. avgjørelser som ble tatt før man tok i bruk IKT-baserte løsninger for journalføring og arkivering. ','B','Obligatorisk for løsninger hvor presedenser inngår','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.29','Det skal være mulig å identifisere den eller de journalpostene i en saksmappe som inneholder presedensavgjørelsen.','B','Obligatorisk for løsninger hvor presedenser inngår','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.30','Registrering, endring og tilgang til presedenser skal styres av tilgangsrettigheter.','B','Obligatorisk for løsninger hvor presedenser inngår','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.31','Følgende statuser for Presedens er obligatoriske:','B','Obligatorisk for løsninger hvor presedenser inngår','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','”Gjeldende”','','','','','','','','');
