INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.32','Foreldede presedenser skal ikke kunne slettes.','B','Obligatorisk for løsninger hvor presedenser inngår','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.33','Det skal ikke være mulig å slette en presedens selv om klassen som presedensen tilhører skal kasseres','B','Obligatorisk for løsninger hvor presedenser inngår','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.34','Det skal være mulig å etablere en samlet presedensoversikt i tilknytning til arkivstrukturen.','B','Obligatorisk for løsninger hvor presedenser inngår','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.35','Det skal finnes en tjeneste/funksjon som gir mulighet for å få en fullstendig oversikt over alle presedenser','B','Obligatorisk for løsninger hvor presedenser inngår','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.36','Presedensvedtaket skal kunne presenteres i et offentlig dokument eller i en offentlig variant. ','B','Obligatorisk for løsninger hvor presedenser inngår','','','','','','');
