INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til arkivering uten journalføring','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.37','Når et dokument er under utarbeidelse, bør dokumentet kunne gis en foreløpig tilknytning til løsningen uten at det knyttes til en Saksmappe og formelt sett arkiveres.','V','','Dersom denne funksjonaliteten implementeres, vil faren for dokumentasjonstap øke. Det anbefales å ikke stille dette kravet. Videre anbefales det å ikke implementere funksjonaliteten dersom systemet som velges likevel har funksjonaliteten tilgjengelig.','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.38','Ved ferdigstilling og elektronisk arkivering av et dokument som er tilknyttet systemet etter prinsippene i krav 6.2.37, eller som er produsert utenfor systemet, skal brukeren kunne velge om dokumentet skal journalføres eller kun arkiveres.','B','Obligatorisk hvis Registrering benyttes','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.39','Dokumenter som skal arkiveres uten journalføring skal tilknyttes registreringstypen Registrering.','B','Obligatorisk hvis Registrering benyttes','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.40','For dokumenter som arkiveres uten journalføring skal det ikke være mulig å knytte til andre metadata til Registrering enn de som fremgår av tabellen ”Metadata for Registrering”.','B','Obligatorisk hvis Registrering benyttes','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.41','En Registrering skal kunne påføres Skjerming.','B','Obligatorisk hvis Registrering benyttes','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.42','Det bør være mulig å knytte en dokumentflyt til en Registrering, jf. kapittel 7.6.','V','','Kravet vil kunne bidra til å øke kvaliteten i kommunens dokumentforvaltning, og anbefales stilt.','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.43','Registrering inngår ikke i journalen og skal ikke være med i journalrapporter (herunder offentlig journal) eller andre dokumentlister på tvers av saker.','B','Obligatorisk hvis Registrering benyttes','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.44','Registrering skal kunne vises på dokumentlister i en Saksmappe, og skal da plassers på riktig sted i dokumentkronologien. ','B','Obligatorisk hvis Registrering benyttes','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.2.45','Det skal finnes en funksjon for å angi at en Registrering er ferdigstilt, det vil si at M604 arkivertDato tildeles verdi. ','B','Obligatorisk hvis Registrering benyttes','','','','','','');
