INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.5.42','Innholdet i offentlig journal tilgjengeliggjort på Internett skal samsvare med Arkivforskriften § 2-7 første ledd annet punktum. I tillegg skal det være med et kontakt­punkt som publikum kan henvende seg til hos organet. Se for øvrig offentlegforskrifta § 6','B','Obligatorisk hvis løsningen muliggjør tilgjengeliggjøring på Internett ','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.5.43','Offentlig journal på Internett skal kun inneholde registreringer av typen journalpost.','B','Obligatorisk hvis løsningen muliggjør tilgjengeliggjøring på Internett','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.5.44','Offentlig journal på Internett skal ikke inneholde informasjon som er unntatt fra offentlighet. Denne informasjonen skal allerede være skjermet i løsningen. ','B','Obligatorisk hvis løsningen muliggjør tilgjengeliggjøring på Internett','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.5.45','Følgende informasjon skal aldri gjøres tilgjengelig på Internett, selv om informasjonen ikke er unntatt offentlighet: ','B','Obligatorisk hvis løsningen muliggjør tilgjengeliggjøring på Internett','','','','','','');
