INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.6.18','For hver arkivdel, klasse, mappe, registrering og dokumentbeskrivelse bør det kunne registreres hvilke personlig identifiserte brukere som har lesetilgang','V','','Eksempelvis lærere som da får lesetilgang, men ikke redigeringstilgang, til elevmapper i sak/arkivsystemet','','','','','');
