INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til tilgangskoder for unntak fra offentlig journal','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.6.32','Det skal kunne registreres tilgangskode på mapper, registreringer og dokumentbeskrivelser. Den angir at registrerte opplysninger eller arkiverte dokumenter skal skjermes mot offentlighetens innsyn','B','Obligatorisk for løsninger hvor informasjon skal unntas fra offentlighet','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.6.33','Alle tilgangskoder som skal brukes må være forhåndsdefinert i kjernen. Tilgangskodene er globale, det vil si at de samme kodene brukes for hele arkivet uavhengig av hvilke eksterne moduler som gjør bruk av arkivet','B','Obligatorisk for løsninger hvor informasjon skal unntas fra offentlighet','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.6.34','Kjernen skal inneholde full historikk over alle tilgangskoder som er eller har vært gyldige i arkivet','B','Obligatorisk for løsninger hvor informasjon skal unntas fra offentlighet','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.6.35','For hver tilgangskode skal det kunne registreres en indikasjon på hvorvidt et dokument som er merket med denne tilgangskoden kan unntas fra offentlighet i sin helhet, eller om det bare er anledning til å unnta bestemte opplysninger fra dokumentet i tråd med det som er angitt i offentleglovas hjemmelsbestemmelse','B','Obligatorisk for løsninger hvor informasjon skal unntas fra offentlighet','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.6.36','Det bør finnes en dedikert tilgangskode for ”midlertidig unntatt”, som kan brukes inntil skjermingsbehov er vurdert','V','','','Krever opplæring og oppfølging slik at ikke opplysninger blir permanent midlertidig unntatt','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.6.37','I tilknytning til en tilgangskode, skal følgende mappeopplysninger i kjernen kunne markeres som ”skjermet” slik at eksterne moduler som leser fra arkivet får følgende begrensninger når tilgangskoden benyttes:','B','Obligatorisk for løsninger hvor informasjon skal unntas fra offentlighet','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','· Deler av mappetittelen: Løsningen skal enten tillate skjerming av alt unntatt første del av tittelen (for eksempel første linje), eller alternativt skjerming av enkeltord som bruker markerer','','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','· Klassifikasjon: Dette er primært beregnet på skjerming av objektkoder som er personnavn eller fødselsnummer','','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','· Opplysninger som identifiserer parter i saken','','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.6.38','I tilknytning til en tilgangskode, skal følgende opplysninger knyttet til registreringer i kjernen kunne markeres som ”skjermet” slik at eksterne moduler som leser fra arkivet får følgende begrensninger når tilgangskoden benyttes:','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','· Deler av innholdsbeskrivelsen: Løsningen skal enten tillate skjerming av alt unntatt første del av innholdsbeskrivelsen (for eksempel første linje), eller alternativt skjerming av enkeltord som bruker markerer','','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','· Opplysninger som identifiserer avsender og/eller mottaker','','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.6.39','Dokumentbeskrivelser knyttet til en registrering skal kunne skjermes. Det skal fremgå at registreringen inneholder dokumentbeskrivelser som er skjermet i journalen','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.6.40','Følgende opplysninger om elektroniske dokumenter skal kunne skjermes ved hjelp av tilgangskode:','O','','','','','','','');
