INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.6.41','Dersom tilgangskoden er merket med indikasjon på at det bare er anledning til å unnta visse opplysninger i dokumentet fra innsyn, kan det opprettes en ”offentlig variant” av dokumentet der disse opplysningene ikke finnes, som derfor kan unntas fra skjerming','V','','','Anbefales - meroffentlighet','','','','');
