INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.7.3','Det skal finnes en oversikt over hvilket eller hvilke tidsrom hver administrative enhet har vært aktiv.','B','Obligatorisk for løsninger hvor administrative enheter skal ha tilgang til objekter i kjernen','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Kjernens krav til Brukeradministrasjon','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.8.1','Alle brukere som skal ha tilgang til enheter i kjernen, skal være identifisert og gjenkjennes av kjernen.','B','Obligatorisk for løsninger hvor personlig identifiserte brukere skal være identifisert i kjernen','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.8.2','Kjernen skal kunne gjenkjenne i hvilken administrativ sammenheng brukeren virker til enhver tid. ','B','Obligatorisk for løsninger hvor personlig identifiserte brukere skal være identifisert i kjernen','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.8.3','En bruker som ikke lenger skal ha tilgang til enheter i kjernen skal fortsatt være identifisert i kjernen, men med en status som indikerer at den er ”passiv”','B','Obligatorisk for løsninger hvor personlig identifiserte brukere skal være identifisert i kjernen','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.8.4','Det skal finnes en oversikt over hvilket eller hvilke tidsrom hver bruker har vært aktiv.','B','Obligatorisk for løsninger hvor personlig identifiserte brukere skal være identifisert i kjernen','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Funksjonelle krav til bruksfunksjonalitet','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.10.1','Det skal legges til rette for at krav til Universell Utforming gitt i lov- og regelverk, blir oppfylt.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.10.2','Det bør være mulig å få meldinger og tekster i begge målformer.','V','','Dette må kommunen vurdere selv. Vær oppmerksom på at det begrensede antallet leverandører kan indikere at kravet ikke bør settes som obligatorisk i kommunens kravspesifikasjon.','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til Dokumentflyt','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.11.1','Et dokument som er under produksjon, bør kunne sendes fram og tilbake i linjen det nødvendige antall ganger.','V','Tidligere krav 7.6.1','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.11.2','Autoriserte roller og personer bør kunne se hvor dokumentet befinner seg til enhver tid.','V','Tidligere krav 7.6.2','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.11.3','Dokumentet bør bli sperret for endringer når det (videre)sendes, ev. det opprettes en ny versjon ved hver (videre)forsendelse.','V','Tidligere krav 7.6.3','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.11.4','Det bør være mulig å registrere merknader til dokumentet.','V','Tidligere krav 7.6.5','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.11.5','Mottaker av et dokument på flyt, bør bli varslet om at han/hun har mottatt et dokument.','V','Tidligere krav 7.6.6','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.11.6','Det bør være mulig å gi en forpliktende «signatur» i alle ledd.','V','Tidligere krav 7.6.7','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.11.7','Det bør være mulig å sende et dokument som er under produksjon, til trinnvis godkjenning (sekvensielt)','V','Tidligere krav 7.6.9','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.11.8','Det bør være mulig å sende et dokument som er under produksjon, til høring til flere samtidig (parallelt)','V','Tidligere krav 7.6.10','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.11.9','For dokument som er under produksjon, og som sendes på sekvensiell eller parallell dokumentflyt, bør det kunne parameterstyres om det automatisk skal opprettes nye versjoner for alle mottakere i flyten.','V','Tidligere krav 7.6.11','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('6.11.10','Det bør kunne parameterstyres om versjonering skal forekomme bare for enkelte roller, enheter, grupper eller personer. Dette skal kunne gjøres fast eller på ad-hoc-basis.','V','Tidligere krav 7.6.12','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til adresseregister i saksbehandlingsløsningen','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.1.1','Det bør finnes funksjoner for å etablere et register med navn og adresser til korrespondansepartnere (adresseregister), og dette skal kunne benyttes til oppslag ved registrering av avsendere, mottakere og parter i en sak.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til saksoppfølging i saksbehandlingsløsningen','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.2.1','Det bør finnes funksjoner for å hente fram eksterne og interne dokumenter fra journalposter som har behandlingsfrist i et angitt tidsrom.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til dokumentproduksjon','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.1','Det bør være mulig for flere personer å utarbeide og redigere et stort dokument samtidig','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.2','Det bør være mulig å opprette dokument og dokumentmaler som fritt bør kunne defineres og seksjoneres i forskjellige deler til ulike brukere/forfattere','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.3','Det bør være mulig å legge til metadata til et dokument i XML-format. Disse skjemaene bør dokumenteres.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.4','Den som oppretter et dokument, dokumenteieren, bør ha rettigheter til å dele opp dokumentet i seksjoner og tildele lese-, kommentar-, remiss- og skriverettigheter til de enkelte seksjonene.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.5','Dokument og seksjoner bør kunne klassifiseres etter egne klassifiseringsskjema som angir skjermingsgrunn.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.6','Det bør kunne angis om markering bør vises for seksjon som bruker ikke har leserettighet til. Markering bør i så fall angi skjermingsgrunn.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.7','Dokumenteier bør kunne tildele annen bruker rollen som dokument- og seksjonseier. ','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.8','For personer som har kommentarrettigheter til et dokument eller seksjon, men ikke skriverettigheter, bør det være mulig å knytte kommentarnotater til dokumentet. Det bør enkelt kunne identifiseres hvilken del av teksten som er kommentert og hvem som har gjort det.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.9','For hver seksjon bør dokumenteier kunne tildele lese-, kommentar-, remiss- og eventuelt skriverettigheter til en enhet/avdeling, en definert rolle eller en definert bruker, heretter som en fellesbenevnelse benevnt «bruker».','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.10','Ved tildeling av lese- og skriverettigheter til en avdeling, bør alle på avdelingen kunne lese, åpne og skrive/redigere dokumentet eller seksjonen(e) som er det gitt rettigheter til. ','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.11','Ved tildeling av lese- og skriverettigheter til en rolle, bør alle som har rollen kunne lese, åpne og skrive/redigere dokumentet eller seksjonen(e) som er det gitt rettigheter til.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.12','Når en bruker har åpnet en seksjon for skriving/redigering, bør den ikke kunne åpnes av andre, dvs. er den låst for lesing og skriving/redigering av andre.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.13','Seksjoner som brukeren kun har leserettigheter til, bør vises med særskilt bakgrunn','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.14','For seksjoner som brukeren kun har leserettigheter til, bør brukeren ikke kunne kopiere den skrivebeskyttede teksten og lime den inn i annet dokument.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.15','Åpne seksjoner eller seksjoner en bruker er tildelt skriverettigheter til, bør vises på ordinær måte.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.16','Seksjoner brukeren ikke har leserettigheter til, bør fjernes fysisk for brukeren.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.17','Når bruker er ferdig med seksjonen og avslutter den, bør låsingen oppheves. Alle med leserettigheter bør kunne lese seksjonen og brukere med skrive-/redigeringsrettigheter bør kunne skrive/redigere i seksjonen.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.18','Det bør være mulig ut fra dokumentoversikten å se hvem som er tildelt, og jobber med et dokument og seksjon.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.19','Et seksjonert dokument bør bare kunne leses av den som har rettigheter til å lese en eller flere seksjoner i dokumentet.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.20','Et seksjonert dokument bør bare kunne åpnes av bruker som dokumenteier har gitt rettigheter til.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.21','Den enkelte bruker bør bare kunne se de deler av dokumentet vedkommende er gitt rettigheter til. Dokumentet vises som et sammenhengende dokument med de seksjoner bruker har rettighet til.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.22','Kun definert dokumenteier eller eier til seksjonene bør kunne endre brukerrettigheter til henholdsvis dokument eller seksjon.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.23','Ikke definerte brukere bør fritt kunne søke i dokumentet, men bør kun få tilgang til å se de delene av dokumentet som er åpne.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.24','Leserettighetene til et dokument som er under arbeid, bør kunne avgrenses til roller og personer som gjennom parametersetting har generelle rettigheter til dette, eller de roller og personer som dokumenteier gir eksplisitte rettigheter.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.25','Det bør være parameterstyrte muligheter til definere leserettighetene til alle dokumenter som er under arbeid.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.26','Det bør være parameterstyrte muligheter til definere leserettighetene til ett eller flere bestemte dokument som er under arbeid, spesifikt for det enkelte dokumentet.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.27','Det bør være mulig for dokumenteier å overstyre parametersettingen.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.28','Når et egenprodusert dokument som er under arbeid sendes på arbeidsflyt eller dokumentflyt, bør alle mottakere av flyten automatisk autoriseres for lesetilgang til dokumentet uavhengig av rolle, administrativ enhet eller autorisasjon.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.29','Det bør være tilgang til funksjoner for opprettelse og forhåndsregistrering av et dokument både fra tekstbehandler og fra Komplett Noark5.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.3.30','Hvis dokumenter forhåndsregistreres fra tekstbehandler bør det være unødvendig å skifte til Komplett Noark5. Brukeren bør ved opprettelse og lukking av et dokument automatisk spørres om nødvendige opplysninger for å registrere dokumentet.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til dokumentmaler','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.4.1','Det bør finnes funksjoner for å etablere dokumentmaler for alle dokumenttyper som en virksomhet benytter.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.4.2','Det bør finnes funksjoner for å utforme og bruke egendefinerte dokumentmaler.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.4.3','Det bør finnes funksjoner for å etablere nye dokumentmaler.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.4.4','Det bør finnes funksjoner for at virksomheten selv kan lage/utarbeide dokumentmaler uten ekstern bistand.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.4.5','Det bør være mulig å legge inn to kategorier standardtekst i dokumentmalene, tekst som kan endres av saksbehandler og tekst som ikke kan endres av saksbehandler.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.4.6','Informasjon fra felt i Komplett Noark5 bør kunne overføres automatisk til korresponderende felt i dokumentmalen.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.4.7','Informasjon fra et felt i dokumentmalen bør kunne overføres automatisk til korresponderende felt i Komplett Noark5.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.4.8','Ved skjerming av dokumenter, bør henvisning til hjemmel kunne skrives inn eller velges fritt av saksbehandler og løsningen plasserer teksten på angitt sted i dokumentmalen','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til saks- og dokumenthistorikk','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.5.1','Det bør være mulig, på en enkel måte, å spore fakta for den enkelte sak, både uavsluttete og avsluttete.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.5.2','Det bør være mulig å se hva som har vært gjort med saken, når det har skjedd og hvem som utførte handlingen (person eller løsning).','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.5.3','Det bør være mulig å se nye saksmapper eller dokumenter som ikke er fordelt, påbegynt eller åpnet.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.5.4','Det bør være mulig å se hva som er status for saksmappen eller dokumentet, dvs. om det har status ikke er fordelt, status ikke påbegynt eller status ikke åpnet.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.5.5','Det bør være mulig å se at saksmapper og dokumenter ligger hos saksbehandler og venter på handling fra ham/henne.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.5.6','Det bør være mulig å se at saksmapper og dokumenter er sendt til godkjenning internt.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.5.7','Det bør være mulig å se at saksmapper eller dokumenter er til behandling hos et eksternt organ, f. eks. hos klageorgan, annet behandlende organ eller i rettsapparatet.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.5.8','Det bør være mulig å la alle dokumenter som inngår i saksmappen samt referanser til andre dokumenter (fra Internett, sakarkivet med mer) som inngår i et saksforhold, følge saksmappen.','V','','','','','','','');
