INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til Arbeidsflyt','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.7.1','Det bør være mulig å ekspedere (sende) en oppgave, en sak eller et dokument som er ferdig fra saksbehandler på arbeidsflyt internt.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.7.2','Det bør være mulig å legge opp en arbeidsflyt (både forhåndsdefinert og ad-hoc) med forhåndsdefinerte aktiviteter og milepæler.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.7.3','Det bør være mulig å forhåndsdefinere både sekvensiell arbeidsflyt og samtidig arbeidsflyt','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.7.4','Det bør være mulig å legge inn kontrollfunksjonalitet for de enkelte aktiviteter, f. eks. at arbeidsoppgaven eller saken ikke går videre i flyten før alle aktiviteter er utført.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.7.5','Det bør være mulig å legge inn tidsbestemte aktiviteter, for eksempel hvis en sak ikke er åpnet på et angitt antall dager så skal den automatisk sendes videre i løsningen til en alias e.l.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.7.6','Det bør være mulig å ekspedere (sende) en oppgave, en sak eller et dokument som er ferdig fra saksbehandler, på arbeidsflyt internt.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.7.7','Det bør være mulig å ekspedere (sende) en oppgave, en sak eller et dokument som er ferdig fra saksbehandler, på trinnvis arbeidsflyt internt, dvs. at det automatisk sendes videre til neste trinn når det foregående er ferdig med oppgaven.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.7.8','Det bør være mulig for alle mottakere i den trinnvise, interne arbeidsflyten å knytte en merknad eller et dokument til den opprinnelige oppgaven, saken eller dokumentet.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.7.9','Når et dokument som er ferdig fra saksbehandler sendes på arbeidsflyt internt, bør dokumentet som er i arbeidsflyten ikke kunne endres eller slettes. ','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.7.10','Det bør være mulig å overstyre den forhåndsdefinerte arbeidsflyten av autorisert personale.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.7.11','Det bør være mulig å gi en forpliktende «signatur» i alle ledd.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.7.12','Sporingselementer fra en arbeidsflyt bør logges og bevares. Disse sporingselementene er:','V','','','','','','','');
