INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','Hvordan har den enkelte mottaker håndtert oppgaven/saken/dokumentet (f. eks. godkjent/ikke godkjent)','','','','','','','','');
