INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('7.7.13','Det bør finnes en funksjon for å søke fram eksterne og interne dokumenter med behandlingsfrist i et angitt tidsrom.','V','','','','','','','');
