INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.1.1','I en komplett Noark5-løsning bør det være full integrasjon med organets e-postsystem, enten med en intern (innebygd) e-postløsning eller gjennom en sømløs integrert (ekstern) e-postløsning for organet.','V','','','','','','','');
