INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','Merknad: Dette kan eksempelvis være saksbehandlende avdelings e-postadresse eller saksbehandlers e-postadresse','','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.3.8','Overstyring av ”avsenders e-postadresse” bør kunne gjøres også på permanent basis.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til angivelse av mottaker(e) for ekspedering','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.3.9','Ved ekspedering av saksdokumenter via e-post bør løsningen sette opp alle ”predefinerte mottakere” (mottakerliste) som er knyttet til aktuelle Registrering som forslag til e-post mottaker.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.3.10','Muligheten for å endre e-postens mottakerliste bør være tilgjengelig som en funksjon under ekspedering av e-post.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.3.11','Hvis e-postadressene ved en ekspedering endres i forhold til standard mottakerliste bør det lagres informasjon om dette.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.3.12','Det bør være mulig å manuelt overstyre predefinert e-postadresse i mottakerfeltet ved ekspedering.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr.','Krav til saksdokumenter som skal utgå fra ekspedering','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.3.13','Ved ekspedering av dokumenter per e-post bør det finnes en tjeneste/funksjon for å angi hvilke dokumenter som skal utgå fra ekspederingen.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.3.14','Det bør finnes en tjeneste/funksjon for å registrere merknader til de saksdokumenter som ikke medfølger en ekspedering.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.3.15','Ved ekspedering bør løsningen generere meldingstekst som gir kort informasjon om de saksdokumenter som eventuelt ikke medfølger e-postforsendelsen.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.3.16','For de saksdokumenter som ikke medfølger ekspedering bør systemgenerert meldingstekst samt eventuelle merknader registrert av saksbehandler alltid medfølge e-postekspederingen.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr:','Krav til ekspederingskontroll','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.3.17','Noark5-løsningen skal ikke tillate at dokumenter ekspederes per e-post i strid med de begrensninger som er gitt av tilgangs- og rettighetsdata og mottakers autorisasjon for samme.','B','Obligatorisk for løsning med e-postintegrasjon','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.3.18','Ved ekspedering bør det utføres kontroll mot utvekslingsformatet.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.3.19','Dersom dokumentformater og/eller bruk av elektronisk signatur ikke samsvarer med utvekslingsformatets opplysninger om mottaker, skal ekspederingen avbrytes og man skal få en feilmelding.','B','Obligatorisk for løsning med e-postintegrasjon','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.3.20','Avbrutt ekspedering bør likevel kunne sendes når den godkjennes av saksbehandler med autorisasjon til å utføre den prosessen.','V','Obligatorisk for løsning med e-postintegrasjon','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr:','Krav til formatering av saksdokumenter','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.3.21','Ved ekspedering og forsendelse av saksdokumenter per e-post skal det være mulig å parametersette at arkivformatet benyttes som standardformat.','B','Obligatorisk for løsning med e-postintegrasjon','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.3.22','Det skal være mulig å velge om produksjonsformatet skal benyttes ved ekspedering og forsendelse av saksdokumenter per e-post.','B','Obligatorisk for løsning med e-postintegrasjon','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr:','Krav til registrering av dokumenter mottatt per e-post','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.4.1','Informasjon fra e-posten, e-posthodet, e-postmelding og vedlegg bør på en oversiktelig måte presenteres tilrettelagt for videre registrering av hele eller deler av e-postforsendelsen.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.4.2','Det bør være mulig for saksbehandler å se innholdet i vedleggene til en e-post via en funksjon for hurtigvisning, eller med lenke til programmet som kan lese filene.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.4.3','Det bør være mulig for saksbehandler å angi hva som skal registreres som hoveddokument og hva som skal være vedlegg fra en postforsendelse.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.4.4','Det bør være mulig å velge at kun enkelte vedlegg til forsendelsen som mottas, skal registreres. Selve e-posten skal alltid registreres.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.4.5','Det bør være mulig å dokumentere i merknadsfelt eller lignende hvorfor enkelte vedlegg ikke arkiveres, om det skyldes dokumentformat eller andre årsaker.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.4.6','Det bør være mulig at det for en saksbehandler kan settes opp en systemparameter som resulterer i at saksbehandler enten automatisk blir satt som saksansvarlig ved oppretting av ny mappe, eller ikke blir satt som saksansvarlig ved oppretting av ny mappe. Det bør være mulig å overstyre dette.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.4.7','Det bør være mulig at det settes opp en systemparameter som gjør at saksbehandler enten automatisk blir satt som ansvarlig saksbehandler ved oppretting av ny registrering, eller ikke blir satt som ansvarlig saksbehandler ved oppretting av ny registrering. Det bør være mulig å overstyre dette.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.4.8','Noark5- bør ha funksjonalitet for at det er avsenders/mottakers/kopimottakers ”display navn” (hvis det finnes) som inngår i det arkiverte dokumentet som dannes på basis av e-posthode og e-postmelding.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.4.9','Noark5- bør ha funksjonalitet for at det er avsenders/mottakers/kopimottakers ”display navn” (hvis det finnes) som inngår som avsender/mottakermetadata i journalen.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr:','Krav til opprettelse av ny mappe','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.4.10','Det bør være mulig å velge om man vil opprette en ny mappe eller bruke en eksisterende mappe via en direkte tilgjengelig funksjon under journalføring / arkivering av innkommende e-post.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr:','Krav til fremsøking av mappe','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.4.11','Det bør være mulig å søke frem en mappe/ en registrering via en hurtigsøksfunksjon basert på saksnummer, avsender, dato eller emne (innhold) for å registrere e-post. Denne muligheten bør være direkte tilgjengelig som en funksjon under registrering av e-post.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr:','Krav til kopi av saksdokumenter per e-post','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.5.1','Det skal være mulig å sende en samlet kopi av et, flere eller alle saksdokumenter knyttet til en registrering med e-post i en og samme forsendelse.','B','Obligatorisk for løsning med e-postintegrasjon','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.5.2','Det skal kunne logges i Noark5-løsningen at dokumentene er sendt som kopi.','B','Obligatorisk for løsning med e-postintegrasjon','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.5.3','Det bør være mulig å sende kopi av en mappe, det vil si alle hoveddokument og vedlegg fra alle registreringer i mappen.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.5.4','Det bør være mulig å beholde strukturen ved en slik forsendelse slik at det er mulig å se hvilke saksdokumenter som henger sammen.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.5.5','Det bør være mulig å sende kopi og velge ut at bare hoveddokumentet sendes.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr:','Krav til sikkerhet','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.1','Det skal finnes funksjoner som gjør det mulig for et organ å etablere ønsket sikkerhetsnivå.','O','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr:','Krav til sikkerhetshåndtering av inn- og utgående e-post','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.2','Innkommende e-post bør alltid kontrolleres opp mot de sikkerhetsmekanismer som er definert for Noark5-løsningen.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.3','Noark5-løsningen bør kunne kvittere for mottatt e-post med og uten vedlegg (ut fra kvitteringsregler i løsningen).','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.4','Det bør være mulig å bruke automatisk kvittering på e-post (for eksempel idet e-post/dokument registreres).','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.5','Kvittering for at mottaker har mottatt e-posten, bør være tilgjengelig og forståelig for saksbehandler som har sendt e-posten.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.6','Hvis automatisk kvittering for mottak av e-post mottak er brukt, bør det fremgå at kvitteringen er automatisk.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.7','Noark5-løsningen bør ha en funksjon for å tilføre kvitteringsopplysninger (levert, åpnet/lest, feilet) fra e-postsystemet til Noark5-løsningens mottakeropplysninger på registreringen.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.8','Utgående e-post bør alltid kontrolleres opp mot de sikkerhetsmekanismer som er definert for Noark5-løsningen.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.9','Sikkerhetsnivået i Noark5-løsningen bør gjøre det mulig å hindre at dokumenter sendes via e-post.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.10','Saksbehandler bør få varsel om tilgangskode og generelle begrensninger som er satt på gitte dokumenter (mapper, klasseringer) dersom disse forsøkes ekspedert per e-post.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.11','Det bør være mulig å sette opp roller og brukere av Noark5-løsningen på en slik måte at de kan overstyre sperrer mot utsendelse som er definert som standard.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.12','Innenfor et arkiv bør sperrer mot utsendelse kunne spesifiseres på ethvert hierarkisk nivå i arkivstrukturen. Sperrer mot utsendelse skal arves av underliggende nivå.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.13','Det bør være mulig å sette opp Noark5-løsningen slik at skjermede dokumenter kan sendes til bestemte, predefinerte mottakere.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr:','Krav til tidsstempling av e-post','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.14','Noark5-løsningen bør ha sikkerhetsfunksjoner som manuelt eller automatisk kan dokumentere i ettertid når en e-post ble sendt ut fra eller kom inn til organet ut fra definerte loggingsregler i tilknytning til arkiv, arkivdel, mappe eller dokument.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr:','Krav til ikke-benekting ved bruk av e-post','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.15','Noark5-løsningen bør kunne ha sikkerhetsfunksjoner som sikrer at meldingen virkelig ble levert til adressat ("proof of delivery").','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.16','Noark5-løsningen bør kunne ha sikkerhetsfunksjoner som kan bevise at avsender virkelig sendte aktuell e-post, selv om avsender kan ønske å benekte det ("non-repudiation of origin").','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.17','Noark5-løsningen bør kunne ha sikkerhetsfunksjoner som kan bevise at avsender virkelig har mottatt aktuell e-post, selv om mottaker kan ønske å benekte det ("non-repudiation of delivery").','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr:','Krav til konfidensialitet','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.18','Noark5-løsningen bør kunne ha sikkerhetsfunksjoner som sikrer at kun adressaten skal kunne lese e-postmelding og tilhørende dokumenter ("content confidentiality").','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.19','Noark5-løsningen bør kunne ha sikkerhetsfunksjoner som kan sikre at alle brukere kan ha egen e-postadresseliste ("message flow confidentiality").','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.20','Noark5-løsningen bør kunne ha sikkerhetsfunksjoner som kan sikre at ingen kan bruke andre sine e-postadresser uten å være gitt autorisasjon. ("secure access management").','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.21','Noark5-løsningen bør kunne ha sikkerhetsfunksjoner som manuelt eller automatisk kan adressere mottakere i ”blindkopi” (bcc=blind carbon copy)[1] for utgående e-post med dokumenter ut fra definerte adresseringsregler i tilknytning til arkiv, mappe eller dokument.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.22','Et organ skal ha mulighet til å velge bort funksjonen for å adressere e-post til mottakere i ”blindkopi”.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('[1] Ved utsendelse av en e-post til mange mottakere kan det være hensikts­messig å bruke blindkopi. Blindkopi eller bcc (Blind Carbon Copy) gjør det mulig å” skjule” hvem som er mottakere av e-postmeldingen. I motsetning til adresser i "Til:" og "Kopi til:"-feltene, er ikke adressene i blindkopi-feltet synlige for andre mottakere av e-posten.','','','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr:','Krav til kryptering av e-post','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.23','Noark5-løsningen bør kunne ha sikkerhetsfunksjoner som manuelt eller automatisk kan håndtere kryptering av utgående e-post med dokumenter (vedlegg) og dekryptering av innkommende e-post med dokumenter ut fra definerte krypteringsregler i tilknytning til arkiv, mappe eller dokument.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.24','Innenfor et arkiv bør regler for kryptering av dokumenter kunne spesifiseres på et hvert hierarkisk nivå i arkivstrukturen i Noark5-løsningen.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.25','Regler for kryptering av dokumenter skal arves av underliggende nivå i arkivstrukturen.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.26','Det bør være mulig å kunne sette opp både roller og brukere av Noark5-løsningen på en slik måte at de kan overstyre reglene for kryptering ved forsendelse som er definert som standard.','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('Krav nr:','Krav til integritetssikring ved elektronisk signering','Type','Merknad','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.27','Noark5-løsningen bør kunne ha sikkerhetsfunksjoner som manuelt eller automatisk kan digitalt signere utgående e-post med dokumenter ut fra definerte signeringsregler i tilknytning til arkiv, mappe, eller dokument (”authenticity”)[1].','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.28','Noark5-løsningen bør kunne ha sikkerhetsfunksjoner som kan verifisere at avsender av en innkommende e-post er den han utgir seg for å være (”authenticity”).','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('8.6.29','Noark5-løsningen bør kunne ha sikkerhetsfunksjoner som kan verifisere at dokumentene ikke er endret etter ekspedering via e-post ("content integrity")[2].','V','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('[1] Autentisering er her brukt i betydningen: Å verifisere (fastslå) påstått identitet til avsender eller mottaker i et kommunikasjonssystem.','','','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('[2] ”Content integrity” eller dataintegritet innebærer at mottaker kan verifisere at innholdet i et dokument ikke er blitt endret fra det tidspunkt meldingen ble avsendt til den ble mottatt.','','','','','','','','','');
