INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','• SAKSBEHANDLING, fordelt på "Innkommet", "Utgående" og "Avskrevet".','','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','• RESTANSER, fordelt på "Nye i perioden", "Eldre" og "Totalt"','','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','– "Innkommet" er definert som "alle inngående dokument (og valgfritt organinterne dokument for oppfølging som har kommet inn) som er journalført i restanseperioden".','','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','– "Utgående" er definert som "alle utgående dokument (og valgfritt organinterne dokument for oppfølging som har blitt avsendt) som er journalført i restanseperioden".','','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','– "Avskrevet" er definert som "alle inngående dokument (og valgfritt organinterne dokument) med avskrivningsdato i restanseperioden"','','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','– "Nye i perioden" er definert som "alle inngående dokument (og valgfritt organinterne dokument for oppfølging) med journaldato innenfor restanseperioden som mangler avskrivningsdato".','','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','– "Eldre" er definert som "alle inngående dokument (og valgfritt organinterne dokument) med journaldato før restanseperioden som mangler avskrivningsdato".','','','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','– "Totalt" er definert som summen av "Nye i perioden" og "Eldre".','','','','','','','','');
