INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','– "Nye saksmapper i perioden" er definert lik "alle med saksdato innenfor den valgte perioden, som skal følges opp (det vil si som ikke har utgått eller har blitt avsluttet, og inneholder uavskrevne journalposter)".','','','','','','','','');
