INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('','– "Uavsluttede saker totalt" er definert som summen av "Herav uavsluttet" og "Uavsluttede eldre saker".','','','','','','','','');
