INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('13.3.1','Sporingsinformasjon for revisjon og etterprøving av tilgangskontrollen skal lagres som eksterne logger utenfor Noark5 kjernen, på egne filer, i egne databaser eller lignende','B','Obligatorisk hvis 13.2.1 og 13.2.2 oppfylles','','','','','','');
INSERT INTO kravspec (kravnr, ookrav, kravtype, merknad, forklaring, konsekvens, konfnivaa, refkrav, status, ansvar) VALUES ('13.3.2','All pålogging for databaseadministrator, brukere med rett til å endre installasjon/programmoduler, eller andre brukere med administratorrettigheter i Noark5 kjernen, skal logges med følgende informasjon:','B','Obligatorisk hvis 13.2.1 og 13.2.2 oppfylles','','','','','','');
